select csp.service_provider_entity_id , fs.field_key, fs.field_source,  fs.field_value from field_string fs inner join compound_service_provider csp on csp.id = fs.compound_service_provider_id where fs.field_value is not null and TRIM(fs.field_value) <> '' and fs.field_source = 1 and (fs.field_key = 18 or fs.field_key = 19) INTO OUTFILE '/tmp/wiki_url.csv' FIELDS TERMINATED BY ',';

select csp.service_provider_entity_id, ls.value, ls.locale, ls2.value, ls.locale from facet_value_compound_service_provider fvcsp inner join compound_service_provider csp on csp.id = fvcsp.compound_service_provider_id inner join facet_value fv on fv.id = fvcsp.facet_value_id inner join multilingual_string ms on ms.id = fv.multilingual_string_id inner join localized_string ls on ls.multilingual_string_id = ms.id inner join facet f on f.id = fv.facet_id inner join multilingual_string ms2 on ms2.id = f.multilingual_string_id inner join localized_string ls2 on ls2.multilingual_string_id = ms2.id INTO OUTFILE '/tmp/facets.csv' FIELDS TERMINATED BY ',';

select service_provider_entity_id, normenkader_present, license_status, strong_authentication from compound_service_provider INTO OUTFILE '/tmp/compound_service_provider.csv' FIELDS TERMINATED BY ',';
